import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'แนะนำอาหารและของว่าง',
      theme: ThemeData(
        primarySwatch: Colors.blue,
        visualDensity: VisualDensity.adaptivePlatformDensity,
      ),
      home: FoodSnackScreen(),
    );
  }
}

class FoodSnackScreen extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('แนะนำอาหารและของว่าง'),
      ),
      body: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          // ส่วนของอาหาร (4 ช่อง)
          Padding(
            padding: const EdgeInsets.all(8.0),
            child: Text(
              'อาหารแนะนำ',
              style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),
            ),
          ),
          // GridView สำหรับอาหาร
          Expanded(
            child: GridView.builder(
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 4,  // 4 ช่องในแต่ละแถว
                childAspectRatio: 1,  // ทำให้ไอเท็มมีสัดส่วนเหมาะสม
                crossAxisSpacing: 8,  // ระยะห่างระหว่างคอลัมน์
                mainAxisSpacing: 8,  // ระยะห่างระหว่างแถว
              ),
              itemCount: 4,  // จำนวนรายการอาหาร
              itemBuilder: (context, index) {
                final foodItems = [
                  {'image': 'data:image/jpeg;base64,', 'title': 'ข้าวผัด'},
                  {'image': 'data:image/jpeg;base64,', 'title': 'ก๋วยเตี๋ยว'},
                  {'image': 'data:image/jpeg;base64,', 'title': 'ข้าวมันไก่'},
                  {'image': 'data:image/jpeg;base64,', 'title': 'ปลาทอด'},
                ];
                return FoodItem(
                  image: foodItems[index]['image']!,
                  title: foodItems[index]['title']!,
                  onTap: () {
                    // ลิงก์ไปยังหน้ารายละเอียดอาหาร
                    Navigator.push(
                      context,
                      MaterialPageRoute(builder: (context) => FoodDetailScreen(foodItems[index]['title']!)),
                    );
                  },
                );
              },
            ),
          ),

          // ส่วนของของว่าง (3 ช่อง)
          Padding(
            padding: const EdgeInsets.all(8.0),
            child: Text(
              'ของว่างแนะนำ',
              style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),
            ),
          ),
          // GridView สำหรับของว่าง
          Expanded(
            child: GridView.builder(
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 3,  // 3 ช่องในแต่ละแถว
                childAspectRatio: 1,  // ทำให้ไอเท็มมีสัดส่วนเหมาะสม
                crossAxisSpacing: 8,  // ระยะห่างระหว่างคอลัมน์
                mainAxisSpacing: 8,  // ระยะห่างระหว่างแถว
              ),
              itemCount: 3,  // จำนวนรายการของว่าง
              itemBuilder: (context, index) {
                final snackItems = [
                  {'image': 'data:image/jpeg;base64,', 'title': 'ขนมเบื้อง'},
                  {'image': 'data:image/jpeg;base64,', 'title': 'ไข่นกกระทา'},
                  {'image': 'data:image/jpeg;base64,', 'title': 'ขนมไข่'},
                ];
                return SnackItem(
                  image: snackItems[index]['image']!,
                  title: snackItems[index]['title']!,
                  onTap: () {
                    Navigator.push(
                      context,
                      MaterialPageRoute(builder: (context) => SnackDetailScreen(snackItems[index]['title']!)),
                    );
                  },
                );
              },
            ),
          ),
        ],
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {
          // ลิงก์ไปหน้าหลักหรือหน้าต่อไป
        },
        child: Icon(Icons.arrow_forward),
      ),
    );
  }
}

class FoodItem extends StatelessWidget {
  final String image;
  final String title;
  final Function onTap;

  FoodItem({required this.image, required this.title, required this.onTap});

  @override
  Widget build(BuildContext context) {
    return GestureDetector(
      onTap: () => onTap(),
      child: Card(
        elevation: 4.0,
        shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8.0)),
        child: Column(
          children: [
            // Image as a clickable area
            GestureDetector(
              onTap: () => onTap(), // Tap on the image navigates to the detail page
              child: ClipRRect(
                borderRadius: BorderRadius.circular(8.0),
                child: Image.network(image, width: 120, height: 120, fit: BoxFit.cover),
              ),
            ),
            Padding(
              padding: const EdgeInsets.all(8.0),
              child: Text(title, style: TextStyle(fontSize: 16)),
            ),
            // Button under the image for extra navigation
            ElevatedButton(
              onPressed: () => onTap(),
              child: Text('ดูรายละเอียด'),
            ),
          ],
        ),
      ),
    );
  }
}

